static void _cbDialog(WM_MESSAGE * pMsg) {
  WM_HWIN hItem;
  int     NCode;
  int     Id;
  User_data u;

  switch (pMsg->MsgId) {
              
  case WM_INIT_DIALOG:
    InitDialog(pMsg);
    break;
  case WM_KEY:
    hItem = WM_GetDialogItem(DialogWin, ID_BUTTON_0);
    switch(((WM_KEY_INFO*)(pMsg->Data.p))->Key)
    {
        case GUI_KEY_ENTER:
            WM_SetFocus(hItem);
            GUI_SendKeyMsg(GUI_KEY_ENTER,1);
            break;
        default:;break;
    }
    break;
    
  case WM_PAINT:
      GUI_SetBkColor(GUI_LIGHTGRAY);
      GUI_Clear();
      break;      
    
  case WM_NOTIFY_PARENT:
    Id    = WM_GetId(pMsg->hWinSrc);
    NCode = pMsg->Data.v;
    switch(Id) {
    case ID_BUTTON_0: // Notifications sent by 'YES'
      switch(NCode) {
      case WM_NOTIFICATION_CLICKED:
//        hItem = WM_GetDialogItem(pMsg->hWin, ID_EDIT_0);
//        EDIT_GetText(hItem,buf,32);
        User_data_encode(pMsg->hWin);
        NRF24L01_TX_Mode();
        if(NRF24L01_TxPacket(buf)==TX_OK)
        {
            LED0 = ~LED0;
        }
        break;
      case WM_NOTIFICATION_RELEASED:
        break;
      }
      break;
    case ID_BUTTON_1: // Notifications sent by 'NO'
      switch(NCode) {
      case WM_NOTIFICATION_CLICKED:
        break;
      case WM_NOTIFICATION_RELEASED:
        break;
      }
      break;
    }
    break;

  default:
    WM_DefaultProc(pMsg);
    break;
  }

}

