WM_HWIN CreateFramewin(void) {
    
  WM_HWIN hWin;
  
  DialogWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
  hWin = DialogWin;  
  WM_SetCallback(WM_HBKWIN,_cbDesktop);
    
  return hWin;
}


